import React, { useState, useEffect, useLayoutEffect } from "react";
import { useNavigate } from "react-router-dom";
import { useAppContext } from "../context/appContext";

const ProtectedRoute = ({ children }) => {
  let navigate = useNavigate();
  const { isAuthenticated, verifyToken } = useAppContext();

  useLayoutEffect(() => {
    console.log("====================================");
    console.log("Potected Route B: ", isAuthenticated);
    console.log("====================================");

    const persist = async () => {
      const n_token = localStorage.getItem("token");

      if (!n_token) {
        console.log("NO TOKEN");
        return;
      }

      await verifyToken(n_token);
    };
    persist();

    console.log("====================================");
    console.log("Potected Route A: ", isAuthenticated);
    console.log("====================================");

    if (!isAuthenticated) {
      return navigate("/login");
    }
  }, [isAuthenticated]);

  return children;
};

export default ProtectedRoute;
