import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { useAppContext } from "../context/appContext";

const ProtectedRoute = ({ children }) => {
  let navigate = useNavigate();
  const { isAuthenticated, token, verifyToken } = useAppContext();

  useEffect(() => {
    const persist = async () => {
      const p_token = localStorage.getItem("token");
      if (!p_token) {
        console.log("NOOOO TTTTT");
        return;
      }

      // const isP = await verifyToken(p_token);
      // console.log(isP, "POP");
    };
    persist();
    if (!isAuthenticated) {
      console.log(isAuthenticated, "PROTEC ROUTE");
      return navigate("/login");
    }
  }, [isAuthenticated]);

  return children;
};

export default ProtectedRoute;
